import React, { useEffect, useState } from "react";
import "./navBar.css"
import logo from "./logo.png"

function NavBar() {
        const [openGame, setOpenGame] = useState(false)
        const [openCom, setOpenCom] = useState(false)

    useEffect(() => {
        function handleClick() {
            if(openGame) {
                setOpenGame(false)
            } 
            if(openCom) {
                setOpenCom(false)
            }
        }

        document.addEventListener("click", handleClick)
        return function cleanupListener() {
            document.removeEventListener('click', handleClick)
          }

    },[openGame, openCom])

        function dropDownGame() {
            setOpenGame(!openGame)
        }

        function dropDownCom() {
            setOpenCom(!openCom)
        }


        function menuDdGame() {    
            return (<div className="container-dd">
                <li>ABOUT</li>
                <li>STORY</li>
                <li>CLASSES</li>
                <li>MEDIA</li>
                <li>FAQ</li>
            </div>)
        }

      function menuDdCom()  {
            return (<div className="container-ddCom">
                <li>SOCIAL FEED</li>
                <li>CREATOR OF PROGRAM</li>
                <li>LEGENDS OF LOST ARK</li>
                <li>ARMORY EXTENSION</li>
                <li>FORUM</li>
            </div>)
        }

      function menuSupport() {
            return (<div className="container-ddSupp">
                <li>Server status</li>
            </div>)
      }



    return (
        <div className="navBarContainer">
            <ul className="navBar">
            <div className="logo"><img className="logoImg" src={logo} alt="logo"></img></div>
                <li className="navLi" id="game"><button className="liBtn" onClick={() => dropDownGame()}>GAME</button>{openGame ? menuDdGame() : null }</li>
                <li className="navLi"><button className="liBtn" >NEWS</button></li>
                <li className="navLi" id="community"><button className="liBtn" onClick={() => dropDownCom()}>COMMUNITY</button>{openCom ? menuDdCom() : null}</li>
                <li className="navLi"><button className="liBtn">SUPPORT</button></li>
            </ul>
            <button className="packBtn">BUY FOUNDER'S PACK</button>
        </div>
    )
}

export default NavBar;
